import { useState } from "react";
import { useAuthContext } from "../../hooks/useAuthContext";
import useGuestHouse from "../../hooks/useGuestHouse";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import { Images } from "../../Constants/images";

const OwnerAddHouse = () => {
  const [image, setImage] = useState(null);
  const { GuestHouse, isLoading } = useGuestHouse();
  const { user } = useAuthContext();
  const initialize = {
    MaxGuest: "",
    Rooms: "",
    Kitchen: "",
    RentAmount: "",
    LimitDays: "",
    Address: "",
  };
  const [HouseData, setData] = useState(initialize);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setData({ ...HouseData, [name]: value });
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      if (file) {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => {
          resolve(reader.result);
        };
        reader.onerror = (err) => {
          reject(err);
        };
      } else {
        resolve(null);
      }
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await GuestHouse({
      OwnerEmail: user.Email,
      OwnerName: user.Name,
      MaxGuest: HouseData.MaxGuest,
      Room: HouseData.Rooms,
      Kitchen: HouseData.Kitchen,
      RentAmount: HouseData.RentAmount,
      LimitDays: HouseData.LimitDays,
      Address: HouseData.Address,
      HouseImg: image,
    });
  };

  return (
    <div className="h-screen w-screen flex justify-center items-center" style={{ backgroundImage:`url(${Images.BG})` }}>

    
    <div>
      <form className="bg-white rounded-lg p-[30px] w-[500px] flex flex-col gap-[20px]" >
        <div>
          <img src={Images.Owner} className="h-[150px]"/>
        </div>
        <TextField
          label="Max Guest"
          name="MaxGuest"
          onChange={handleChange}
          placeholder="Enter the Max Guest"
        />
        <TextField
          label="Rooms"
          name="Rooms"
          onChange={handleChange}
          placeholder="Enter the Rooms"
        />
        <TextField
          label="Kitchen"
          name="Kitchen"
          onChange={handleChange}
          placeholder="Enter the Kitchen"
        />
        <TextField
          label="Rent Amount"
          name="RentAmount"
          onChange={handleChange}
          placeholder="Enter the Rent Amount"
        />
        <TextField
          label="Limit Days"
          name="LimitDays"
          onChange={handleChange}
          placeholder="Enter the Limit Days"
        />
        <TextField
          label="Address"
          name="Address"
          onChange={handleChange}
          placeholder="Enter the Address"
        />
        <input
          type="file"
          id="file-input"
          onChange={(e) => convertToBase64(e.target.files[0]).then(setImage)}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={handleSubmit}
          disabled={isLoading}
          startIcon={<CloudUploadIcon />}
          style={{ marginTop: "10px" }}
        >
          Submit
        </Button>
        {image == null ? "" : <img src={image} alt="House Preview" style={{ maxWidth: "100%", height: "auto", marginTop: "10px" }} />}
      </form>
    </div>
    </div>
  );
};

export default OwnerAddHouse;
