import { useState } from "react";
import useSignup from "../hooks/useSignup";
import { useLogout } from "../hooks/useLogout";
import { Stack, TextField, Button, Container, Box } from "@mui/material";
import useMediaQuery from '../hooks/useMediaQuery'
const Login = () => {
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const { signups, isloading, iserror } = useSignup();
  const Media = useMediaQuery();
  const { logout } = useLogout();
  const HandleLogin = async (e) => {
    e.preventDefault();
    const action = "userlogin";
    const types = "user";
    await signups({ Email, Password, action, types });
  };
  const HandleLogout = () => {
    logout();
  };
  return (
    <section>
      <Container maxWidth={"lg"}>
        <Box height={"80vh"} display={"flex"} justifyContent={"center"} alignItems={"center"}>
          <div className={`h-[${Media?"600px":"300px"}] w-[500px] bg-primarycolor`}>
            <TextField />
            <TextField />
            <Button></Button>
          </div>
        </Box>
      </Container>
    </section>
  );
};

export default Login;
