import { useContext, useState } from 'react';
import { useAuthContext } from './useAuthContext';
import { useNavigate } from 'react-router-dom'
const useSignup = () => {
    const [iserror, setError] = useState(null);
    const [isloading, setLoading] = useState(null);
    const { dispatch } = useAuthContext();
    const location = useNavigate();
    const signups = async ({ Email, Password, action, Name, PhoneNumber, type }) => {
        console.log({ Email, Password, Name, PhoneNumber, action, type });
        console.log(action);
        setError(null);
        setLoading(true);
        const Fetch = await fetch(`http://localhost:4000/${action}`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json" 
            },
            body: JSON.stringify({ Email, Password, Name, PhoneNumber })
        });

        const JsonData = await Fetch.json();
        console.log(JsonData);
        if (!Fetch.ok) {
            // Handle the Error because if the user is not available
            setLoading(false);
            setError(JsonData.Error);
        } else {
            // Save the user to localStorage for authentication purpose
            setLoading(false);
            localStorage.setItem('User', JSON.stringify(JsonData));
            dispatch({ type: "Login", payload: JsonData });
            location(`/`);
        }
    }
    return { signups, isloading, iserror };
}

export default useSignup;
