import { useState } from "react"
import { useSignup } from '../hooks/useSignup'

const Signup = () => {
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const HandleSignup = async (e) => {
      e.preventDefault();
      console.log(Email);
      console.log(Password);
      fetch('http://localhost:4000/signup',{
        method: "POST",
        headers: {
            "Content-Type":"application/json"
        },
        body:JSON.stringify({ Email, Password }),
      }).then(res => {
       return res.json();
      }).then(data => {
        localStorage.setItem('User', data);
        console.log(data)
      });
  }
  return (
    <div>
      <input type={"text"} onChange={(e) => setEmail(e.target.value)} placeholder="Enter your name" required/>
      <input type={"password"} onChange={(e) => setPassword(e.target.value)} placeholder="Enter your Password" required/>
      <button onClick={(e) => HandleSignup(e) }>Login</button>
    </div>
  )
}

export default Signup
