import { useState } from "react";
const OwnerAddHouse = () => {
    const [image, setImage] = useState(null);
    const converToBase64 = (e) => {
      var reader = new FileReader();
      reader.readAsDataURL(e.target.files[0]);
      reader.onload = () => {
        setImage(reader.result);
        console.log(reader.result);
      };
      reader.onerror = (err) => {
        console.log("Error : ", err);
      };
    };
    const initalize = {
        OwnerEmail:'',
        OwnerName:'',
        MaxGuest:'',
        Rooms:'',
        Kitchen:'',
        RentAmount: '',
        LimitDays: '',
        Address: '',
    }
    const [HouseData, setData] = useState(initalize);
    const HandleChange = (e) => {   
        const {name , value} = e.target;
        setData({...HouseData, [name]: value });
        console.log(HouseData);
    }
    const HandleSubmit = (e) => {
        e.preventDefault();

    }
  return (
    <div>
      <div>
        <input type="email" name="OwnerEmail" onChange={(e) => HandleChange(e)} placeholder="enter the Email" />
      </div>
      <div>
        <input type="text" name="OwnerName" onChange={(e) => HandleChange(e)} placeholder="Enter the name" />
      </div>
      <div>
        <input type="text" name="MaxGuest" onChange={(e) => HandleChange(e)} placeholder="Enter the name" />
      </div>
      <div>
        <input type="text" name="Rooms" onChange={(e) => HandleChange(e)} placeholder="Enter the name" />
      </div>
      <div>
        <input type="text" name="Kitchen" onChange={(e) => HandleChange(e)} placeholder="Enter the name" />
      </div>
      <div>
        <input type="text" name="RentAmount" onChange={(e) => HandleChange(e)} placeholder="Enter the name" />
      </div>
      <div>
        <input type="text" name="LimitDays" onChange={(e) => HandleChange(e)} placeholder="Enter the name" />
      </div>
      <div>
        <input type="text" name="Address" onChange={(e) => HandleChange(e)} placeholder="Enter the name" />
      </div>
      <div>
        <input type="file" onChange={converToBase64}/>
      </div>
      <button onClick={(e) => HandleSubmit(e)}>Submit</button>
      { image==null?"": <img src={image}/>}
    </div>
  );
};

export default OwnerAddHouse;
