import { useState } from "react";
import useSignup from "../hooks/useSignup";
import { useLogout } from "../hooks/useLogout";
import { Stack, TextField, Button, Container, Box } from "@mui/material";
import useMediaQuery from "../hooks/useMediaQuery";
const Login = () => {
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const { signups, isloading, iserror } = useSignup();
  const Media = useMediaQuery("(min-width:1070px)");
  const { logout } = useLogout();
  const HandleLogin = async (e) => {
    e.preventDefault();
    const action = "userlogin";
    const types = "user";
    await signups({ Email, Password, action, types });
  };
  const HandleLogout = () => {
    logout();
  };
  return (
    <section>
      <Container maxWidth={"lg"}>
        <Box
          height={"80vh"}
          display={"flex"}
          justifyContent={"center"}
          alignItems={"center"}
        >
          <div
            className={`h-[${
              Media ? "600px" : "80%"
            }] w-[${Media?"500px":"300px"}] ${Media?"bg-primarycolor":"bg-primarycolor"} rounded-2xl flex justify-center items-center flex-col ${Media?"m-[20px]":"m-[5px]"}`}
            
          >
            <div className="">
              <TextField />
            </div>
            <div>
              <TextField />
            </div>
            <Button></Button>
          </div>
        </Box>
      </Container>
    </section>
  );
};

export default Login;
