import { useState } from 'react';
import { useAuthContext } from './useAuthContext';
import { json, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify';
import axios from 'axios'; // Import Axios

const useGuestHouse = () => {
    const [iserror, setError] = useState(null);
    const [isloading, setLoading] = useState(null);
    const { dispatch, user } = useAuthContext();
    const location = useNavigate();
    const GuestHouse = async ({ OwnerEmail, OwnerName,  MaxGuest, Room, Kitchen, RentAmount, LimitDays, Address, HouseImg }) => {
        console.log({ OwnerEmail, OwnerName,  MaxGuest, Room, Kitchen, RentAmount, LimitDays, Address, HouseImg });
        setError(null);
        setLoading(true);
        axios.defaults.maxContentLength = 5000000;
        try {
            const response = await fetch('http://localhost:4000/guesthousedata', {
                method:"POST",
                headers: {
                    "Authorization": `Bearer ${user.token}`,
                    "Content-Type": "application/json",
                },
                body:JSON.stringify({
                    OwnerEmail,
                    OwnerName,
                    MaxGuest,
                    Room,
                    Kitchen,
                    RentAmount,
                    LimitDays,
                    Address,
                    HouseImg:HouseImg
                }) 
            });

            console.log(response.data);

            if (response.data.Error) {
                toast.error(response.data.Error);
            } else {
                toast.success("Successfully stored");
                location('/ownerhome');
            }
            setLoading(false);
        } catch (error) {
            console.error(error.Error);
            setLoading(false);
            toast.error('An error occurred while saving the data.');
        }
    }
    return { GuestHouse, isloading, iserror };
}

export default useGuestHouse;
