import { useContext, useState } from 'react';
import { useAuthContext } from './useAuthContext';


const useSignup = () => {
    const [ iserror, setError ] = useState(null);
    const [ isloading, setLoading ] = useState(null);
    const { dispatch } = useContext(AuthContext);

    const signups = async ( Email, Password ) => {
        setError(null);
        setLoading(true);
        const Fetch = await fetch('http://localhost:4000/signup', {
            method: "POST",
            headers: {
                "Context-Type" : "application/json"
            },
            body:JSON.stringify({ Email, Password })
        });

        const JsonData = await Fetch.json();

        if(!Fetch.ok){
            // Handle the Error because if the user is not available
            setLoading(false);
            setError(JsonData.Error);
        }
        else{
             // Save the user to localStorage for authendication purpose
            setLoading(false);
            localStorage.setItem('User', JSON.stringify(JsonData));
            dispatch({ type: "Login", payload : JsonData })
        }
        return { signups, isloading, iserror }
    }
}

export default useSignup;
