import { useState } from "react";
import useSignup from "../hooks/useSignup";
import { useLogout } from "../hooks/useLogout";
import { Stack, Input, InputAdornment, TextField, Button, Container, Box } from "@mui/material";
import { AccountCircle } from '@mui/icons-material'
import useMediaQuery from "../hooks/useMediaQuery";
const Login = () => {
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const { signups, isloading, iserror } = useSignup();
  const Media = useMediaQuery("(min-width:1070px)");
  const { logout } = useLogout();
  const HandleLogin = async (e) => {
    e.preventDefault();
    const action = "userlogin";
    const types = "user";
    await signups({ Email, Password, action, types });
  };
 
  const HandleLogout = () => {
    logout();
  };
  return (
    <section>
      <Container maxWidth={"lg"}>
        <Box
          height={"80vh"}
          display={"flex"}
          justifyContent={"center"}
          alignItems={"center"}
        >
          <div
            className={`h-[600px] w-[300px] bg-primarycolor`}
            
          >
            <div className="my-[20px] w-[70%]">

            </div>
            <div className="">
            <TextField
          error
          id="outlined-error-helper-text"
          color=""
          label="Password"
          defaultValue="Hello World"
          helperText="Incorrect Password."
        />
            </div>
            <Button></Button>
          </div>
        </Box>
      </Container>
    </section>
  );
};

export default Login;
