import { useState } from 'react';
import { useAuthContext } from './useAuthContext';
import { useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify';
const useGuestHouse = () => {
    const [iserror, setError] = useState(null);
    const [isloading, setLoading] = useState(null);
    const { dispatch } = useAuthContext();
    const location = useNavigate();

    const GuestHouse = async ({ OwnerEmail, OwnerName,  MaxGuest, Room, Kitchen, RentAmount, LimitDays, Address, HouseImg }) => {
        console.log({ Email, Password, Name, PhoneNumber, action, types });
        console.log(action);
        setError(null);
        setLoading(true);
        const Fetch = await fetch(`http://localhost:4000/${action}`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json" 
            },
            body: JSON.stringify({ OwnerEmail, Name, PhoneNumber, types })
        });

        const JsonData = await Fetch.json();
        console.log(JsonData);
        if (!Fetch.ok) {
            // Handle the Error because if the user is not available
            setLoading(false);
            setError(JsonData.Error);
            toast.error(iserror)
        } else {
            // Save the user to localStorage for authentication purpose
            setLoading(false);
            localStorage.setItem('User', JSON.stringify(JsonData));
            dispatch({ type: "Login", payload: JsonData });
            toast.success("Successfully Loggedin")
            location(`/${types==="user"?"home":"ownerhome"}`);
        }
    }
    return { GuestHouse, isloading, iserror };
}


export default useGuestHouse
