import { useState } from "react"
const Signup = () => {
  const [Name, setName] = useState("");
  const [Password, setPassword] = useState("");
  const HandleSignup = async (e) => {
      e.preventDefault();
      console.log(Name);
      console.log(Password);
      const Send = await fetch('http://localhost:4000/signup',{
        method: "POST",
        headers: {
            "Content-Type":"application/json"
        },
        body:JSON.stringify({ Name, Password }),
      }).then(res => {
        console.log(res);
      })
  }
  return (
    <div>
      <input type={"text"} onChange={(e) => setName(e.target.value)} placeholder="Enter your name" required/>
      <input type={"password"} onChange={(e) => setPassword(e.target.value)} placeholder="Enter your Password" required/>
      <button onClick={(e) => HandleSignup(e) }>Login</button>
    </div>
  )
}

export default Signup
