import React, { useEffect, useState } from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Container,
  Button,
  CircularProgress, // Import CircularProgress for the loading spinner
} from "@mui/material";
import { useAuthContext } from "../../hooks/useAuthContext";

const OwnerHome = () => {
  const [data, setdata] = useState([]);
  const [loading, setLoading] = useState(true); // State for loading
  const { user } = useAuthContext();
  const houseId = JSON.parse(localStorage.getItem("User"));
  useEffect(() => {
    fetch(`http://localhost:4000/getguesthome/${houseId.Email}`, {
      headers: {
        Authorization: `Bearer ${user.token}`,
      },
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data.Data)
        setdata(data.Data);
        setLoading(false);
      });
  }, []);

  const backgroundStyle = {
    background: "linear-gradient(135deg, #FFD700, #FF5733, #6AFB92)",
    padding: "20px",
    minHeight: "100vh",
  };

  return (
    <div style={backgroundStyle}>
      <Container maxWidth="xl">
        <TableContainer component={Paper}>
          <Table
            style={{
              minWidth: 650,
            }}
          >
            <TableHead>
              <TableRow>
                <TableCell
                  style={{
                    fontWeight: "bold",
                  }}
                >
                  HouseImage
                </TableCell>
              

                <TableCell
                  style={{
                    fontWeight: "bold",
                  }}
                >
                  From Date
                </TableCell>
                <TableCell
                  style={{
                    fontWeight: "bold",
                  }}
                >
                  To Date
                </TableCell>
                <TableCell
                  style={{
                    fontWeight: "bold",
                  }}
                >
                  Edit
                </TableCell>
                <TableCell
                  style={{
                    fontWeight: "bold",
                  }}
                >
                  Delete
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
             
              {loading ? (
                <TableRow>
                  <TableCell colSpan={8} style={{ textAlign: "center" }}>
                    <CircularProgress />
                  </TableCell>
                </TableRow>
              ) : (
                data.map((data) => (
                  <TableRow key={data.id}>
                    <TableCell>
                      <img src={data.HouseImg[0]} alt="house" style={{ height:"200px", width:"200px"}} /></TableCell>
                    <TableCell>{data.Address}</TableCell>
                    <TableCell>{data.FromDate}</TableCell>
                    <TableCell>{data.ToDate}</TableCell>
                    <TableCell>
                      <Button color="primary" variant="contained">
                        Edit
                      </Button>
                    </TableCell>
                    <TableCell>
                      <Button color="error" variant="contained">
                        Delete
                      </Button>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
    </div>
  );
};

export default OwnerHome;
