import { useState } from "react";
import { useAuthContext } from '../../hooks/useAuthContext'
import useGuestHouse from "../../hooks/useGuestHouse";

const OwnerAddHouse = () => {
  const [image, setImage] = useState(null);
  const { GuestHouse, isloading } = useGuestHouse();
  const { user } = useAuthContext();
  const initalize = {
    MaxGuest: '',
    Rooms: '',
    Kitchen: '',
    RentAmount: '',
    LimitDays: '',
    Address: '',
  }
  const [HouseData, setData] = useState(initalize);

  const HandleChange = (e) => {
    const { name, value } = e.target;
    setData({ ...HouseData, [name]: value });
  }

  const Multhandle = (files) => {
    const formData = new FormData();
    for (let index = 0; index < files.length; index++) {
      formData.append("files", files[index]);
    }
    return formData;
  }

  const uploadFiles = async (formData) => {
    try {
      const response = await fetch('http://localhost:4000/image', {
        headers:{
            "Authorization":`Bearer ${user.token}`
        },
        method: 'POST',
        body: formData,
      });
      if (response.ok) {
        const data = await response.json();
        return data; // Replace with the actual response structure from your server
      } else {
        throw new Error('File upload failed');
      }
    } catch (error) {
      console.error(error);
      return [];
    }
  };

  const HandleSubmit = async (e) => {
    e.preventDefault();
    
    const fileInput = document.getElementById("file-input");
    const uploadedFilePaths = await uploadFiles(Multhandle(fileInput.files));
    
    await GuestHouse({ 
      OwnerEmail: user.Email,
      OwnerName: user.Name,
      MaxGuest: HouseData.MaxGuest,
      Room: HouseData.Rooms,
      Kitchen: HouseData.Kitchen,
      RentAmount: HouseData.RentAmount,
      LimitDays: HouseData.LimitDays,
      Address: HouseData.Address,
      HouseImg: uploadedFilePaths,
    });
  }

  return (
    <div>
      <div>
        <input type="text" name="MaxGuest" onChange={(e) => HandleChange(e)} placeholder="Enter the MaxGuest" />
      </div>
      <div>
        <input type="text" name="Rooms" onChange={(e) => HandleChange(e)} placeholder="Enter the Rooms" />
      </div>
      <div>
        <input type="text" name="Kitchen" onChange={(e) => HandleChange(e)} placeholder="Enter the Kitchen" />
      </div>
      <div>
        <input type="text" name="RentAmount" onChange={(e) => HandleChange(e)} placeholder="Enter the RentAmount" />
      </div>
      <div>
        <input type="text" name="LimitDays" onChange={(e) => HandleChange(e)} placeholder="Enter the LimitDays" />
      </div>
      <div>
        <input type="text" name="Address" onChange={(e) => HandleChange(e)} placeholder="Enter the name" />
      </div>
      <div>
        <input type="file" id="file-input" multiple />
      </div>
      <button disabled={isloading} onClick={(e) => HandleSubmit(e)}>Submit</button>
      {image == null ? "" : <img src={image} />}
    </div>
  );
};

export default OwnerAddHouse;
