import { Box, Container, Stack } from "@mui/material";

//import { styled } from '@mui/system';
import useMediaQuery from "../hooks/useMediaQuery";
import { Images } from "../Constants/images";
import Bg from '../Assets/herobg.png'
//import { motion } from "framer-motion";
import { Typewriter } from "react-simple-typewriter";
const Header = () => {
  const Media = useMediaQuery("(min-width:1070px)");
  const backgroundStyle = {
    backgroundImage: `url(${Bg})`, // Set the background image
    backgroundSize: 'cover', // Optional: Adjust background size to cover the entire element
    backgroundRepeat: 'no-repeat', // Optional: Prevent background image repetition
  };
  return (
    <div style={{
      width: '100%',
      height: `${Media ? '100vh' : '100%'}`,
      ...backgroundStyle, // Apply the background style
    }}
>
      <Box
        className="ml-[5%] w-[100%] h-[100%] flex justify-center items-center"
        width={`${Media ? "90%" : "90%"}`}
      >
        <Stack
          direction={`${Media ? "row" : "column"}`}
          spacing={4}
          width={`${Media ? "100%" : "100%"}`}
          height={`${Media ? "85%" : "80%"}`}
          display={"flex"}
          justifyContent={"space-between"}
          style={{
            //backgroundColor: "black",
            padding: `${Media ? "50px" : "20px"}`,
          }}
        >
          <Stack
            width={`${Media ? "700px" : "100%"}`}
            
            className="rounded-2xl m-[20px]"
            height={`${Media ? "80%" : "70% "}`}
            maxHeight={`${Media ? "100%" : "600px"}`}
          >
            {/* <div className="w-[100%] m-[30px] text-black mt-[90px]">
              <h1 className="font-google text-5xl text-secondarycolor font-bold">
                {" "}
                <span className=" text-yellow-400">
                  Flash
                </span>{" "}
                Rooms 
              </h1>
            </div> */}
            <div>
              <Stack margin={""}>
                {/* <img src={Images.Quots}/> */}
                <div>
                  <p className="text-lightblue text-4xl font-google">
                      Every House where love abides and friendship is a Guest
                  </p>
                </div>
                <div></div>
              </Stack>
            </div>
          </Stack>
          <Stack
            width={`${Media ? "900px" : "100%"}`}
            style={{ backgroundColor: "" }}

            height={`${Media ? "100% " : "80% "}`}
          >
            <Stack direction={"column"} width={""}></Stack>
            <div className={`ml-[${Media?"150px":"0"}]`}>
              
              <img src={Images.House} width={"100%"} height={"100%"} alt="Houes" />
            </div>
          </Stack>
        </Stack>
      </Box>
    </div>
  );
};

export default Header;
