import { Container, Stack } from "@mui/material";
import { Images } from "../Constants/images";
import { Search } from "@mui/icons-material";
import { useState } from "react";
import dayjs from "dayjs";
import { DemoContainer, DemoItem } from "@mui/x-date-pickers/internals/demo";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DateRangeCalendar } from "@mui/x-date-pickers-pro/DateRangeCalendar";

const Home = () => {
  const Styles = {
    backgroundImage: `url(${Images.BG})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    height: "100%",
    width: "100%",
  };

  const [range, setRange] = useState({
    startDate: new Date(),
    endDate: new Date(),
  });
  const [Searchval, setSearchval] = useState("");
  const [value, setValue] = useState([
    dayjs("2022-04-17"),
    dayjs("2022-04-21"),
  ]);

  // Define custom styles for the date in the calendar
  const dateStyles = {
    color: "white", // Set the font color to white
  };

  return (
    <section className="h-[120vh] w-screen">
      <div style={Styles}>
        <Container maxWidth="lg">
          <div className="w-[100%] h-[100%] ">
            <Stack
              direction={"column"}
              style={{ width: "100%", height: "780px" }}
            >
              <div className="my-[100px]">
                <h1 className="font-hometext text-center text-7xl text-primarycolor">
                  Your journey begins here
                </h1>
              </div>
              <div className="px-[20px]">
                <div
                  style={{
                    width: "100%",
                    height: "650px",
                    backgroundColor: "white",
                  }}
                  className="p-[30px] rounded-lg  "
                >
                  <Stack direction={"column"}>
                    <div className="h-[60px] my-[10px] w-[100%] p-[10px] ">
                      <h1 className="text-2xl font-mono text-primarycolor underline">
                        All Houses
                      </h1>
                    </div>
                    <div className="h-[70px] p-[10px] bg-gray-200 rounded-lg">
                      <Stack direction={"row"}>
                        <div className="p-[10px]">
                          <Search style={{ color: "#0a0a27" }} />
                        </div>
                        <div className="w-[100%]">
                          <input
                            type="text"
                            placeholder="Search..."
                            className="bg-transparent p-[15px] w-[100%] outline-none text-xl text-primarycolor"
                          />
                        </div>
                      </Stack>
                    </div>
                    <div className="my-[20px] h-[420px] bg-white text-primarycolor">
                      <h1>CheckIN and CheckOut Date</h1>
                      <Stack direction={"row"}>
                        
                        <div>
                          <LocalizationProvider dateAdapter={AdapterDayjs}>
                            <DemoContainer
                              components={[
                                "DateRangeCalendar",
                                "DateRangeCalendar",
                              ]}
                            >
                              <DemoItem >
                                <DateRangeCalendar
                                  value={value}
                                  onChange={(newValue) => setValue(newValue)}
                                />
                              </DemoItem>
                            </DemoContainer>
                          </LocalizationProvider>
                        </div>
                        <div></div>
                      </Stack>
                    </div>
                  </Stack>
                </div>
              </div>
            </Stack>
          </div>
        </Container>
      </div>
    </section>
  );
};

export default Home;
