import { useState } from "react"
import useSignup from "../hooks/useSignup";
import { useLogout } from '../hooks/useLogout'
const Signup = () => {
  const [Email, setEmail] = useState("");
  const [Name, setName] = useState("");
  const [Password, setPassword] = useState("");
  const { signups, isloading, iserror } = useSignup();  
  const { logout } = useLogout();
  const HandleSignup = async (e) => {
      e.preventDefault();
      const action = "signup";
      await signups({ Email, Password, Name, action });
  }
  const HandleLogout = () => {
    logout();
  }
  return (
    <div>
      <input type={"text"} onChange={(e) => setName(e.target.value)} placeholder="Enter your Name" required/>
      <input type={"text"} onChange={(e) => setEmail(e.target.value)} placeholder="Enter your Email" required/>
      <input type={"password"} onChange={(e) => setPassword(e.target.value)} placeholder="Enter your Password" required/>
      <button disabled={isloading} onClick={(e) => HandleSignup(e) }>Login</button>
      {iserror && <div>{ iserror }</div>}
      <button onClick={() => HandleLogout()}>Logout</button>
    </div>
  )
}

export default Signup
