import { Box, Container, Stack, useMediaQuery } from "@mui/material";
import { motion } from "framer-motion";

const Header = () => {
  const Media = useMediaQuery("(min-width:1070px)")
  return (
    <div>
      <Container maxWidth={"lg"}>
        <Box
          width={"100%"}
          height={"100vh"}
          display={"flex"}
          justifyContent={"center"}
          alignItems={"center"}
        
        >
        <Stack spacing={"4"} direction={`${Media?"row":"column"}`} width={`${Media ? "100%":"80%"}`}  display={"flex"} justifyContent={"space-between"} style={{ backgroundColor:"black" }} >
          <Stack width={`${Media?"500px":"80%"}`}>
            <h1 className="text-white">hello</h1>
          </Stack>
          <Stack spacing={"20px"} width={`${Media?"500px":"80%"}`} style={{ backgroundColor:"blue" }} height={`${Media?"500px":"200px"}`}>
            <h1 className="">hello hii </h1></Stack>
        </Stack>
        </Box>
      </Container>
    </div>
  );
};

export default Header;
