import { useState } from "react";
import useSignup from "../hooks/useSignup";
import { Images } from "../Constants/images";
import { useLogout } from "../hooks/useLogout";
import { TextField, Button, Container, Box } from "@mui/material";
import { AccountCircle } from "@mui/icons-material";
import { toast } from 'react-toastify'
import useMediaQuery from "../hooks/useMediaQuery"; // Import your custom CSS file
const Login = () => {
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const { signups, isloading, iserror } = useSignup();
  const Media = useMediaQuery("(min-width:1070px)");
  const { logout } = useLogout();
  console.log({ Email, Password })
  const HandleLogin = async (e) => {
    e.preventDefault();
    const action = "userlogin";
    const types = "user";
    await signups({ Email, Password, action, types });
  };
  const error = (msg) => toast.error(msg);
  const success = (msg) => toast.success(msg);
  const HandleLogout = () => {
    logout();
  };

  return (
    <section className="bg-primarycolor h-[92vh]">
      <Container maxWidth={"lg"}>
        <Box
          height={"80vh"}
          display={"flex"}
          justifyContent={"center"}
          alignItems={"center"}
        >
          <div
            className={`h-[550px] p-[10px] w-[500px] relative bg-white flex-col flex rounded-lg items-center justify-center`}
          >
            <div className={`w-[200px]  h-[100px] mb-[75px]`}>
              <img src={Images.Guest} alt="Houes" />
            </div>
            <div className="my-[20px] w-[80%]">
              <TextField
                fullWidth
                id="outlined-error-helper-text"
                color="primary"
                label="Email"
                placeholder="Enter your Email"
                // Apply your custom CSS class
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="my-[20px]  w-[80%]">
              <TextField
                fullWidth
                id="outlined-error-helper-text"
                color=""
                label="Password"
                placeholder="Enter your Password"
                onChange={(e) => setPassword(e.target.value)}
                // Apply your custom CSS class
              />
            </div>
            <div className="mt-[30px]">
              <button disabled={isloading} className="  bg-secondarycolor text-white px-[25px] py-[10px] rounded-md hover:translate-y-[-6px] duration-500 text-xl" onClick={(e) => HandleLogin(e)} >
                Login
              </button>
              <p>{ iserror }</p>
            </div>
          </div>
        </Box>
      </Container>
    </section>
  );
};

export default Login;
