const useGuestHouse = () => {
    const [iserror, setError] = useState(null);
    const [isloading, setLoading] = useState(null);
    const { dispatch, user } = useAuthContext();
    const location = useNavigate();
    const GuestHouse = async ({ OwnerEmail, OwnerName,  MaxGuest, Room, Kitchen, RentAmount, LimitDays, Address, HouseImg }) => {
        console.log({ OwnerEmail, OwnerName,  MaxGuest, Room, Kitchen, RentAmount, LimitDays, Address, HouseImg });
        setError(null);
        setLoading(true);
        const Fetch = await fetch(`http://localhost:4000/guesthousedata`, {
            method: "POST",
            headers: {
                "Authorization" : `Bearer ${user.token}`,
                "Content-Type": "application/json",

            },
            body: JSON.stringify({ OwnerEmail, OwnerName,  MaxGuest, Room, Kitchen, RentAmount, LimitDays, Address, HouseImg })
        }).then((res) => {
            return res.json();
        }).then((data) => {
            console.log(data);
            toast.success("Succssfully stored")
        }).catch((err) => {
            toast.error(err);
        })
    }
    return { GuestHouse, isloading, iserror };
}


export default useGuestHouse
