import { Box, Container } from "@mui/material";
import Stack from '@mui/system/Stack';
import  useMediaQuery  from '../hooks/useMediaQuery';

import { motion } from "framer-motion";

const Header = () => {
  const Item = styled('div')(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#262B32' : '#fff',
    padding: theme.spacing(1),
    textAlign: 'center',
    borderRadius: 4,
  }));
  const Media = useMediaQuery("(min-width:1070px)")
  return (
    <div>
      <Container maxWidth={"xl"}>
        <Box
          width={"100%"}
          height={"100vh"}
          display={"flex"}
          justifyContent={"center"}
          alignItems={"center"}
        
        >
        <Stack direction={`${Media?"row":"column"}`} spacing={4} width={`${Media ? "100%":"80%"}`}  display={"flex"} justifyContent={"space-between"} style={{ backgroundColor:"black" }} >
          <Item>
            <Stack width={`${Media?"700px":"80%"}`}  height={`${Media? "650px " : "60% "}`}>
              <h1 className="text-white">hello</h1>
            </Stack>
          </Item>
            <Item>
            <Stack width={`${Media?"700px":"80%"}`}  height={`${Media? "650px " : "60% "}`}>
              <h1 className="text-white">hello</h1>
            </Stack>
            </Item>
          </Stack>
        </Box>
      </Container>
    </div>
  );
};

export default Header;
