import {
  Button,
  Container,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Stack,
  TextField,
} from "@mui/material";
import { Images } from "../Constants/images";
import { Search } from "@mui/icons-material";
import { useEffect, useState } from "react";
import dayjs from "dayjs";
import { DemoContainer, DemoItem } from "@mui/x-date-pickers/internals/demo";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DateRangeCalendar } from "@mui/x-date-pickers-pro/DateRangeCalendar";
import { useAuthContext } from "../hooks/useAuthContext";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import {
  Bed,
  MeetingRoomIcon,
  Kitchen,
  AddReaction,
} from "@mui/icons-material";
import axios from "axios";

const Home = () => {
  const location = useNavigate();
  const Styles = {
    backgroundImage: `url(${Images.BgImg})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    height: "100%",
    width: "100%",
    backgroundColor: "#0a0a27",
  };
  const handleBook = (e) => {
    console.log(e.id);
    console.log(e.name);
  };
  const { user } = useAuthContext();
  const [range, setRange] = useState({
    startDate: new Date(),
    endDate: new Date(),
  });
  const [Searchval, setSearchval] = useState("");
  const [Guest, setGuest] = useState("");
  const [Load, isLoaded] = useState(false);
  const [houses, sethouse] = useState();
  const [Room, setRooms] = useState("");
  const [value, setValue] = useState([
    dayjs(range.startDate),
    dayjs(range.endDate),
  ]);
  const start = value[0]?.$d ? value[0].$d : null;
  const end = value[1]?.$d ? value[1].$d : null;
  var From, To, date, date1;
  if (start) {
    date = new Date(start);
    const options = { year: "numeric", month: "short", day: "2-digit" };
    From = date.toLocaleDateString("en-US", options);
  }
  if (end) {
    date1 = new Date(end); //const date1 = new Date(end);
    const options = { year: "numeric", month: "short", day: "2-digit" };
    To = date1.toLocaleDateString("en-US", options); //const To = date1.toLocaleDateString('en-US', options);
  }
  From = dayjs(From);
  To = dayjs(To);

  const daysCount = To.diff(From, "day");
  // Define custom styles for the date in the calendar
  useEffect(() => {
    axios
      .get("http://localhost:4000/getallhouses", {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      })
      .then((response) => {
        if (response.status !== 200) {
          throw new Error("Network response was not ok");
        }
        return response.data;
      })
      .then((result) => {
        isLoaded(true);
        sethouse(result.HouseDatas);
        const Houses = JSON.stringify(result);
        localStorage.setItem("houses", Houses);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, []);

  console.log(houses);
  const [open, setOpen] = useState(false);
  const [ids, setid] = useState("");
  const [datas, setdatas] = useState(1500);
  const handleClickOpen = (e) => {
    // setrent(e.target.name);
    // setid(e.target.names);
    // console.log(e.target.name);
    setdatas(e);

    setOpen(true);
  };
const Booking = () => {

}
  //console.log(datas);
  const handleClose = () => {
    setOpen(false);
  };
  useEffect(() => {
    const currentDate = new Date();
    console.log(date);
    if (date < currentDate) {
      toast.error("Please set greater than current date");
    }
    if (daysCount >= 30) {
      toast.error("Please set less than 30 Days");
    }
  }, [From, To]);

  const HandleBook = (e) => {
    const Bookdate = {
      id: e.target.id,
      FromDate: date,
      ToDate: date1,
      userName: user.Name,
      userEmail: user.Email,
    };
    e.preventDefault();
    localStorage.setItem("Bookid", JSON.stringify(Bookdate));
    location("/BookHouse");
  };
  return (
    <section className="h-[100%]  w-screen">
      {open && (
        <div>
          <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogTitle id="alert-dialog-title">
              {" Please check the Amount "}
            </DialogTitle>
            <DialogContent>
            <Stack direction={"row"}>
            <div className="mx-[10px]">
                
                <TextField
                  value={datas.OwnerName}
                  helperText={"This is the Owner Name "}
                  disabled
                  fullWidth
                />
              </div>
              <div>
                <TextField value={datas.OwnerEmail} helperText={"This is the Owner Email"} disabled fullWidth />
              </div> 
            </Stack>
            <Stack direction={"row"}>
                   
              <div className="mx-[10px]">
                <TextField
                  value={datas.RentAmount}
                  
                  helperText={"This is the money "}
                  disabled
                  fullWidth
                  
                />
              </div>
              <div>
                <TextField
                  value={datas.Address}
                  helperText={"This is the Owner Address of the house "}
                  disabled
                  fullWidth
                />
              </div>
            </Stack>
             
            </DialogContent>

            <DialogActions style={{ margin:"20px" }}>
              <Button onClick={handleClose} variant="contained" color="error">Disagree</Button>
              <Button onClick={Booking} variant="contained" color="primary" autoFocus>
                Continune Booking
              </Button>
            </DialogActions>
          </Dialog>
        </div>
      )}
      <div style={Styles}>
        <Container maxWidth="lg">
          <div className="w-[100%] h-[100%]  ">
            <Stack
              direction={"column"}
              style={{ width: "100%", height: "950px" }}
            >
              <div className="my-[100px]">
                <h1 className="font-hometext text-center text-7xl text-primarycolor">
                  Your journey begins here
                </h1>
              </div>
              <div className="px-[20px] h-[100%]">
                <div
                  style={{
                    width: "100%",
                  }}
                  className="p-[30px] rounded-lg h-[650px] g"
                >
                  <Stack direction={"column"}>
                    <div className="h-[60px] my-[10px] w-[100%] p-[10px] ">
                      <h1 className="text-2xl font-mono text-center text-white underline">
                        Max House in Coimbator
                      </h1>
                    </div>
                    <div className="my-[20px] h-[420px] bg-white rounded-lg text-primarycolor">
                      <h1 className="text-center text-2xl font-google">
                        CheckIN and CheckOut Date
                      </h1>
                      <Stack direction={"row"}>
                        <div>
                          <LocalizationProvider dateAdapter={AdapterDayjs}>
                            <DemoContainer
                              components={[
                                "DateRangeCalendar",
                                "DateRangeCalendar",
                              ]}
                            >
                              <DemoItem>
                                <DateRangeCalendar
                                  value={value}
                                  onChange={(newValue) => setValue(newValue)}
                                />
                              </DemoItem>
                            </DemoContainer>
                          </LocalizationProvider>
                        </div>
                        <div className="h-[200px] w-[200px] text-xl m-[30px]">
                          <div className="p-[10px]">
                            <h1>Rooms and Guest</h1>
                          </div>
                          <hr></hr>
                          <div>
                            <Stack direction={"column"}>
                              <div className="m-[10px]">
                                <p className="mb-[10px]">Rooms</p>
                                <input
                                  type="number"
                                  onChange={(e) => setRooms(e.target.value)}
                                  className="p-[10px] border-[3px] rounded-xl outline-none border-blue-600"
                                  placeholder="Enter the Rooms"
                                />
                              </div>
                              <div className="m-[10px]">
                                <p className="mb-[10px]">Guest</p>
                                <input
                                  type="number"
                                  onChange={(e) => setGuest(e.target.value)}
                                  className="p-[10px] border-[3px] rounded-xl outline-none border-blue-600"
                                  placeholder="Enter the Guest"
                                />
                              </div>
                              <div></div>
                            </Stack>
                          </div>
                        </div>
                      </Stack>
                    </div>
                  </Stack>
                </div>
              </div>
            </Stack>
          </div>
        </Container>
      </div>
      // Existing code ...
      <div className="h-[100%] w-[100%] bg-primarycolor">
        <div className="h-[100%] w-[100%]">
          <Container maxWidth={"lg"}>
            <div className="h-[100%] w-[100%]">
              <Stack direction={"column"}>
                <div className="text-white my-[70px] text-center">
                  <h1 className="text-2xl font-google">
                    Here is what you searched for
                  </h1>
                </div>
                <div className="h-[100%] w-[100%] bg-white rounded-lg p-[30px]">
                  {Load ? (
                    houses
                      .filter((house) => {
                        // Parse the 'LimitDays' value to a number
                        const limitDays = parseInt(house.LimitDays, 10); // Filter houses with 'LimitDays' less than or equal to 'daysCount'
                        return limitDays >= daysCount;
                      })
                      .filter((AvailableDate) => {
                        const availableFromDate = new Date(
                          AvailableDate.FromDate
                        );
                        const availableToDate = new Date(AvailableDate.ToDate);
                        return !(
                          (availableFromDate >= date &&
                            availableFromDate <= date1) ||
                          (availableToDate >= date && availableToDate <= date1)
                        );
                      })
                      .filter((House) => {
                        // Filter houses based on the number of rooms and guests
                        return (
                          parseInt(House.Room, 10) >= parseInt(Room, 10) &&
                          parseInt(House.MaxGuest, 10) >= parseInt(Guest, 10)
                        );
                      })
                      .map((filteredHouse) => (
                        <div key={filteredHouse._id}>
                          <div className="h-[300px] w-[100%] my-[30px]">
                            <Stack direction={"row"}>
                              <div className="mr-[70px]">
                                <img
                                  className="h-[300px] w-[350px] rounded-md"
                                  src={filteredHouse.HouseImg}
                                  alt="House"
                                />
                              </div>
                              <div>
                                <Stack direction={"row"}>
                                  <div className="h-[300px] w-[250px]">
                                    <div className="my-[25px]">
                                      <h1 className="text-2xl">
                                        House Owner Details
                                      </h1>
                                    </div>
                                    <div>
                                      <div className="m-[10px]">
                                        <h1>
                                          Owner Name : {filteredHouse.OwnerName}{" "}
                                        </h1>
                                      </div>
                                      <div className="m-[10px]">
                                        <h1>
                                          Owner Email:{" "}
                                          {filteredHouse.OwnerEmail}
                                        </h1>
                                      </div>
                                      <div className="m-[10px]">
                                        <h1>
                                          Address : {filteredHouse.Address}
                                        </h1>
                                      </div>
                                    </div>
                                  </div>
                                  <div className="h-[300px] w-[250px]">
                                    <div className="my-[25px]">
                                      <h1 className="text-2xl ml-[20px]">
                                        Room Details
                                      </h1>
                                    </div>
                                    <div>
                                      <div className="m-[10px]">
                                        <h1>
                                          Available Rooms :{" "}
                                          <Bed className="mx-[10px]" />{" "}
                                          {filteredHouse.Room}{" "}
                                        </h1>
                                      </div>
                                      <div className="m-[10px]">
                                        <h1>
                                          Available Kitchen : <Kitchen />{" "}
                                          {filteredHouse.Kitchen}
                                        </h1>
                                      </div>
                                      <div className="m-[10px]">
                                        <h1>
                                          Maximum Stay limit :{" "}
                                          {filteredHouse.LimitDays}
                                        </h1>
                                      </div>
                                    </div>
                                  </div>
                                  <div className="h-[300px] w-[200px] flex justify-center items-center">
                                    <div>
                                      <Button
                                        variant="contained"
                                        onClick={() =>
                                          handleClickOpen(filteredHouse)
                                        }
                                        id={filteredHouse._id}
                                        name={filteredHouse.RentAmount}
                                      >
                                        Book
                                      </Button>
                                    </div>
                                  </div>
                                </Stack>
                              </div>
                            </Stack>
                          </div>
                        </div>
                      ))
                  ) : (
                    <p>Loading...</p>
                  )}
                </div>
              </Stack>
            </div>
          </Container>
        </div>
      </div>
    </section>
  );
};

export default Home;
